frappe.ui.form.on('Sales Invoice', {
	before_load (frm) {
	  // your code here
      if (frm.doc.__islocal){
        // do something
        frappe.call({
          method: "frappe.client.get_list",
          args: {
            "doctype": "Sales Invoice",
            "filters": [["status", "!=", "Cancelled"], ["status", "!=", "Draft"]],
            "fields": ["invoice_code", "posting_date"],
            "order_by": "posting_date desc" 
          }, callback: function(r) {
            if (!r.exc) {
              var digit = "0000";
              var id = "FV" + moment(frm.doc.posting_date).format("YY") + "-";
              // get expense task from response
              if (r.message.length === 0) {
                digit = "0001";
