                var ext = +(r.message[len].invoice_code.slice(-4));
                var next = ext + 1;
                var ncode = (digit + next).slice(-4); 
                var res = id + ncode;
                //console.log(res);
                frm.set_value('invoice_code', res);
              }
            }
          }    
        }); 
      }
	},
	refresh (frm) {
	  frm.add_custom_button("Word Template", function() {
        var dialog = new frappe.ui.Dialog({
        title: __('Choose a template file'),
        fields: [
          {
            fieldname: 'template',
            fieldtype: 'Link',
            label: 'File',
            options: 'File',
            filters: {'attached_to_doctype': 'Word Template', 'file_name': ['like', '%.docx']}
