frappe.ui.form.on('Expenses Financial Budget', {
  onload (frm) {
    if (frm.doc.project) {
      frappe.call({
        method: 'project_controls.project_controls.custom.get_time',
        args: {
	      project: frm.doc.project
        },
        callback: (r) => {
          var time = r.message;
          var hours = 0;
          $.each(frm.doc.financial_budget_item, function(index, row){
            if (row.is_time) {
              var i = time.findIndex( ({item_code}) => item_code === row.item_code);
              if ( i >= 0) {
                //console.log(row.doctype, row.name, time[i].Hours);
                frappe.model.set_value(row.doctype, row.name, "qty_ordered", time[i].Hours);
                frappe.model.set_value(row.doctype, row.name, "amount_ordered", time[i].Costs);
                frappe.model.set_value(row.doctype, row.name, "pending_qty", +row.qty - +time[i].Hours);
                hours += +time[i].Hours;
              }
            }  
          });
          //var template = "<p>Horas: <b>" + hours + "</b></p>";
          //console.log(frappe.render_template(template, frm));
          //frm.set_df_property('html_summary', 'options', frappe.render_template(template));
          //frm.refresh_field('html_summary');
          }
      });
      frappe.call({
        method: 'project_controls.project_controls.custom.get_commit',
        args: {
	      project: frm.doc.project
        },
        callback: (r) => {
          var orders = r.message;
          $.each(frm.doc.financial_budget_item, function(index, row){
            if (!row.is_time) {  
              var i = orders.findIndex( ({item_code}) => item_code === row.item_code);
              if ( i >= 0) {
                //console.log(row.doctype, row.name, time[i].Hours);
                frappe.model.set_value(row.doctype, row.name, "qty_ordered", orders[i].Qty);
                frappe.model.set_value(row.doctype, row.name, "amount_ordered", orders[i].Amount);
                frappe.model.set_value(row.doctype, row.name, "pending_qty", +row.qty - +orders[i].Qty);
              }
            }  
          });
        }
      });
      fillValues (frm);
    }
  },
  refresh (frm) {
    fillValues (frm);  
  }
});
frappe.ui.form.on('Financial Budget Item', {
  // cdt is Child DocType name i.e Time Logs
  // cdn is the row name for e.g bbfcb8da6a
  item_code (frm, cdt, cdn) {
    let row = frappe.get_doc(cdt, cdn);
    var arr = frm.doc.financial_budget_item;
    var idx = arr.findIndex(item=>item.item_code === row.item_code);
    var len = arr.length;
    if (idx < 0 || idx === (+len - 1)) {
      if (row.item_code) {
        frappe.call({    
          method: "frappe.client.get_list",
          args: {
            "doctype": "Item",
            "filters": [["item_code", "=", row.item_code]],
            "fields": ["*"]
          }, callback: function(r) {
            if (!r.exc) {
              frappe.model.set_value(cdt, cdn, "expense_task", r.message[0].expense_task);
              frappe.model.set_value(cdt, cdn, "is_time", r.message[0].is_time_activity);
            }
          }    
        });
      } else {
        frappe.model.set_value(cdt, cdn, "expense_task", null);
        frappe.model.set_value(cdt, cdn, "is_time", 0);
      }
    } else {
      frm.doc.financial_budget_item.splice([+len-1], 1);
      frm.refresh_field('financial_budget_item');    
      frappe.msgprint({
        title: __('Notificacion'),
        message: __('Item duplicado con la fila ' + (+idx + 1)),
        action() {
          //location.reload();
          return(false);
        }
      });
    }  
  },
  qty(frm, cdt, cdn) {
    let row = frappe.get_doc(cdt, cdn);
    if (row.qty > 0 && row.unit_rate > 0) {
        var factor = 1;
        var amount = 0;
        if (row.discount > 0) {
          factor = 1 - +row.discount/100;
        }
        amount = (+row.qty * +row.unit_rate) * +factor;
        frappe.model.set_value(cdt, cdn, "amount", amount);
    }
    fillValues (frm);
  },
  unit_rate(frm, cdt, cdn) {
    let row = frappe.get_doc(cdt, cdn);
    if (row.qty > 0 && row.unit_rate > 0) {
        var factor = 1;
        var amount = 0;
        if (row.discount > 0) {
          factor = 1 - +row.discount/100;
        }
        amount = (+row.qty * +row.unit_rate) * +factor;
        frappe.model.set_value(cdt, cdn, "amount", amount);
    }
    fillValues (frm);
  },
  discount(frm, cdt, cdn) {
    let row = frappe.get_doc(cdt, cdn);
    if (row.qty > 0 && row.unit_rate > 0) {
        var amount = 0;
        var factor = 1 - +row.discount/100;
        amount = (+row.qty * +row.unit_rate) * +factor;
        frappe.model.set_value(cdt, cdn, "amount", amount);
    }
    fillValues (frm);
  },
  projection(frm, cdt, cdn) {
    let row = frappe.get_doc(cdt, cdn);
    if (row.projection > 0) {
        var amount = 0;
        var factor = 1 - +row.discount/100;
        amount = (+row.qty * +row.unit_rate) * +factor;
        frappe.model.set_value(cdt, cdn, "amount", amount);
    }
    fillValues (frm);
  },
  financial_budget_item_remove (frm) {
    fillValues (frm);
  }    
});
function fillValues (frm) {
  var financial_item = frm.doc.financial_budget_item;
  var total = 0;
  var cost = 0;
  var projection = 0;
  for (var i in financial_item) {
    total = total + financial_item[i].amount;
    if (frm.doc.project) {
      cost = cost + financial_item[i].amount_ordered;
      projection = projection + financial_item[i].projection;
    }
  }
  $.each(frm.doc.financial_budget_item, function(index, row){
    if (row.qty_ordered === 0) {
      frappe.model.set_value(row.doctype, row.name, "pending_qty", Math.round(+row.qty));  
    } else {
      frappe.model.set_value(row.doctype, row.name, "pending_qty", Math.round(+row.qty - +row.qty_ordered));      
    }
  });
  //console.log(total);
  frm.set_value("total_amount", total);
  frm.set_value("actual_amount", cost);
  frm.set_value("total_projection", projection);
  if (projection > 0) {
    frm.set_value("profit", +total - +projection);  
  }
  if (frm.doc.project) {
    // Show Expenses Summary
    var expenses = [...new Set(financial_item.map(value => value.expense_task))];
    var rtask = expenses.map(function(expense_task){
      var sum = 0; 
      var bqty = 0;
      var cqty = 0;
      var comm = 0;
      $.each(financial_item, function(index, value) {sum += (value.expense_task==expense_task?value.amount:0)} ); 
      $.each(financial_item, function(index, value) {bqty += (value.expense_task==expense_task?value.qty:0)} ); 
      $.each(financial_item, function(index, value) {cqty += (value.expense_task==expense_task?value.qty_ordered:0)} ); 
      $.each(financial_item, function(index, value) {comm += (value.expense_task==expense_task?value.amount_ordered:0)} ); 
      return {expense_task: expense_task, qty: bqty, sum: sum, order: cqty, commit: comm};
    });
    var rtemplate = "";
    for (var j in rtask) {
      rtemplate += '<tr class="grid-row"><td>' + rtask[j].expense_task + '</td><td style="text-align: right;">' + Math.round(rtask[j].qty) + '</td><td style="text-align: right;">' + Math.round(rtask[j].sum) + '</td><td style="text-align: right;">' + Math.round(rtask[j].order) + '</td><td style="text-align: right;">' + Math.round(rtask[j].commit) + '</td></tr>';  
    }
    var template = '<div class="form-grid"><table><tr class="grid-heading-row"><th style="font-size: 9pt; width: 40%;">Tipo<br><i>Type</i></th><th style="font-size: 9pt; width: 10%; text-align: right;">Cantidad<br><i>Quantity</i></th><th style="font-size: 9pt; width: 15%; text-align: right;">Ppto<br><i>Budget</i></th><th style="font-size: 9pt; width: 15%; text-align: right;">Ud.Real<br><i>Act.Un.</i></th><th style="font-size: 9pt; width: 20%; text-align: right;">Real<br><i>Actual</i></th></tr>' + rtemplate + '</table></div>';
    //console.log(template);
    frm.set_df_property('html_summary', 'options', frappe.render_template(template));
    frm.refresh_field('html_summary');
  }
}

